function badge(string $badge_name)
{
    $array_badge = [
        'Enabled' => '<span class="badge badge-success">Habilitado</span>',
        'Disabled' => '<span class="badge badge-danger">Inhabilitado</span>',
        'High' => '<span class="badge badge-danger">Alta</span>',
        'Medium' => '<span class="badge badge-warning">Media</span>',
        'Low' => '<span class="badge badge-success">Baja</span>',
        'Paused' => '<span class="badge badge-warning">Pausada</span>',
        'Started' => '<span class="badge badge-primary">Iniciada</span>',
        'Stoped' => '<span class="badge badge-danger">Detenida</span>',
        'Finished' => '<span class="badge badge-success">Finalizada</span>',
